import React, { useState } from 'react';
import { validateInput } from '../utils/helpers';

const TodoForm = ({ onAddTodo, loading }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // È™åËØÅËæìÂÖ•
    const titleError = validateInput(title, 1, 255);
    if (titleError) {
      setError(titleError);
      return;
    }

    const descriptionError = description ? validateInput(description, 1, 1000) : null;
    if (descriptionError) {
      setError(descriptionError);
      return;
    }

    try {
      setError('');
      await onAddTodo({
        title: title.trim(),
        description: description.trim() || null
      });
      
      // Ê∏ÖÁ©∫Ë°®Âçï
      setTitle('');
      setDescription('');
    } catch (err) {
      setError(err.message || 'Ê∑ªÂä†‰ªªÂä°Â§±Ë¥•');
    }
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
    if (error) setError('');
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
    if (error) setError('');
  };

  return (
    <div className="todo-form">
      <div className="todo-form-header">
        <span className="todo-form-icon">üìù</span>
        <h2 className="todo-form-title">Ê∑ªÂä†Êñ∞‰ªªÂä°</h2>
      </div>
      
      <form onSubmit={handleSubmit}>
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}
        
        <div className="form-row">
          <div className="form-input-group">
            <div className="form-group">
              <label htmlFor="title" className="form-label">
                ‰ªªÂä°Ê†áÈ¢ò *
              </label>
              <input
                type="text"
                id="title"
                className="input"
                placeholder="ËØ∑ËæìÂÖ•‰ªªÂä°Ê†áÈ¢ò..."
                value={title}
                onChange={handleTitleChange}
                disabled={loading}
                maxLength={255}
                required
              />
            </div>
          </div>
        </div>
        
        <div className="form-group">
          <label htmlFor="description" className="form-label">
            ‰ªªÂä°ÊèèËø∞
          </label>
          <textarea
            id="description"
            className="input textarea"
            placeholder="ËØ∑ËæìÂÖ•‰ªªÂä°ÊèèËø∞ÔºàÂèØÈÄâÔºâ..."
            value={description}
            onChange={handleDescriptionChange}
            disabled={loading}
            maxLength={1000}
            rows={3}
          />
        </div>
        
        <div className="form-actions">
          <button
            type="submit"
            className="btn btn-primary btn-lg"
            disabled={loading || !title.trim()}
          >
            {loading ? (
              <>
                <span className="loading-spinner"></span>
                Ê∑ªÂä†‰∏≠...
              </>
            ) : (
              <>
                <span>‚ûï</span>
                Ê∑ªÂä†‰ªªÂä°
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default TodoForm;
