import React, { useState } from 'react';
import { formatDate } from '../utils/helpers';

const TodoItem = ({ todo, onUpdate, onDelete, loading }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(todo.title);
  const [editDescription, setEditDescription] = useState(todo.description || '');

  const handleToggleComplete = async () => {
    try {
      await onUpdate(todo.id, { completed: !todo.completed });
    } catch (err) {
      console.error('Êõ¥Êñ∞‰ªªÂä°Áä∂ÊÄÅÂ§±Ë¥•:', err);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Á°ÆÂÆöË¶ÅÂà†Èô§Ëøô‰∏™‰ªªÂä°ÂêóÔºü')) {
      try {
        await onDelete(todo.id);
      } catch (err) {
        console.error('Âà†Èô§‰ªªÂä°Â§±Ë¥•:', err);
      }
    }
  };

  const handleEdit = () => {
    setIsEditing(true);
    setEditTitle(todo.title);
    setEditDescription(todo.description || '');
  };

  const handleSaveEdit = async () => {
    if (!editTitle.trim()) return;
    
    try {
      await onUpdate(todo.id, {
        title: editTitle.trim(),
        description: editDescription.trim() || null
      });
      setIsEditing(false);
    } catch (err) {
      console.error('Êõ¥Êñ∞‰ªªÂä°Â§±Ë¥•:', err);
    }
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
    setEditTitle(todo.title);
    setEditDescription(todo.description || '');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSaveEdit();
    } else if (e.key === 'Escape') {
      handleCancelEdit();
    }
  };

  return (
    <div className={`todo-item ${todo.completed ? 'completed' : ''}`}>
      <input
        type="checkbox"
        className="todo-checkbox"
        checked={todo.completed}
        onChange={handleToggleComplete}
        disabled={loading}
      />
      
      <div className="todo-content">
        {isEditing ? (
          <div className="edit-form">
            <input
              type="text"
              className="input"
              value={editTitle}
              onChange={(e) => setEditTitle(e.target.value)}
              onKeyDown={handleKeyPress}
              autoFocus
              maxLength={255}
            />
            <textarea
              className="input textarea"
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
              onKeyDown={handleKeyPress}
              placeholder="‰ªªÂä°ÊèèËø∞ÔºàÂèØÈÄâÔºâ"
              maxLength={1000}
              rows={2}
            />
            <div className="edit-actions">
              <button
                className="btn btn-success btn-sm"
                onClick={handleSaveEdit}
                disabled={!editTitle.trim()}
              >
                ‰øùÂ≠ò
              </button>
              <button
                className="btn btn-outline btn-sm"
                onClick={handleCancelEdit}
              >
                ÂèñÊ∂à
              </button>
            </div>
          </div>
        ) : (
          <>
            <h3 className="todo-title">{todo.title}</h3>
            {todo.description && (
              <p className="todo-description">{todo.description}</p>
            )}
            <div className="todo-meta">
              <span>ÂàõÂª∫Êó∂Èó¥: {formatDate(todo.created_at)}</span>
              {todo.updated_at !== todo.created_at && (
                <span>Êõ¥Êñ∞Êó∂Èó¥: {formatDate(todo.updated_at)}</span>
              )}
            </div>
          </>
        )}
      </div>
      
      {!isEditing && (
        <div className="todo-actions">
          <button
            className="todo-action-btn complete"
            onClick={handleToggleComplete}
            disabled={loading}
            title={todo.completed ? 'Ê†áËÆ∞‰∏∫Êú™ÂÆåÊàê' : 'Ê†áËÆ∞‰∏∫ÂÆåÊàê'}
          >
            {todo.completed ? '‚Ü©Ô∏è' : '‚úÖ'}
          </button>
          <button
            className="todo-action-btn edit"
            onClick={handleEdit}
            disabled={loading}
            title="ÁºñËæë‰ªªÂä°"
          >
            ‚úèÔ∏è
          </button>
          <button
            className="todo-action-btn delete"
            onClick={handleDelete}
            disabled={loading}
            title="Âà†Èô§‰ªªÂä°"
          >
            üóëÔ∏è
          </button>
        </div>
      )}
    </div>
  );
};

export default TodoItem;
