import React, { useState, useEffect } from 'react';
import { useTodos } from '../hooks/useTodos';
import { useLocalStorage } from '../hooks/useLocalStorage';
import TodoForm from './TodoForm';
import FilterBar from './FilterBar';
import TodoList from './TodoList';
import ActionBar from './ActionBar';
import LoadingSpinner from './LoadingSpinner';
import { FILTER_STATUS } from '../utils/constants';

const TodoApp = () => {
  const {
    todos,
    loading,
    error,
    filter,
    stats,
    addTodo,
    updateTodo,
    deleteTodo,
    clearCompleted,
    clearAll,
    changeFilter,
    refreshTodos
  } = useTodos();

  // ‰ΩøÁî®Êú¨Âú∞Â≠òÂÇ®‰øùÂ≠òÁ≠õÈÄâÁä∂ÊÄÅ
  const [savedFilter, setSavedFilter] = useLocalStorage('todo-filter', FILTER_STATUS.ALL);

  // Ê∂àÊÅØÁä∂ÊÄÅ
  const [message, setMessage] = useState({ type: '', text: '' });

  // ÊòæÁ§∫Ê∂àÊÅØ
  const showMessage = (type, text) => {
    setMessage({ type, text });
    setTimeout(() => setMessage({ type: '', text: '' }), 3000);
  };

  // ÂàùÂßãÂåñÊó∂ÊÅ¢Â§ç‰øùÂ≠òÁöÑÁ≠õÈÄâÁä∂ÊÄÅ
  useEffect(() => {
    if (savedFilter !== filter) {
      changeFilter(savedFilter);
    }
  }, [savedFilter, filter, changeFilter]);

  // ‰øùÂ≠òÁ≠õÈÄâÁä∂ÊÄÅÂà∞Êú¨Âú∞Â≠òÂÇ®
  const handleFilterChange = (newFilter) => {
    changeFilter(newFilter);
    setSavedFilter(newFilter);
  };

  // Â§ÑÁêÜÊ∑ªÂä†‰ªªÂä°
  const handleAddTodo = async (todoData) => {
    try {
      await addTodo(todoData);
      showMessage('success', '‰ªªÂä°Ê∑ªÂä†ÊàêÂäüÔºÅ');
    } catch (err) {
      showMessage('error', err.message || 'Ê∑ªÂä†‰ªªÂä°Â§±Ë¥•');
    }
  };

  // Â§ÑÁêÜÊõ¥Êñ∞‰ªªÂä°
  const handleUpdateTodo = async (id, updates) => {
    try {
      await updateTodo(id, updates);
      if (updates.completed !== undefined) {
        const action = updates.completed ? 'ÂÆåÊàê' : 'ÂèñÊ∂àÂÆåÊàê';
        showMessage('success', `‰ªªÂä°Â∑≤${action}ÔºÅ`);
      } else {
        showMessage('success', '‰ªªÂä°Êõ¥Êñ∞ÊàêÂäüÔºÅ');
      }
    } catch (err) {
      showMessage('error', err.message || 'Êõ¥Êñ∞‰ªªÂä°Â§±Ë¥•');
    }
  };

  // Â§ÑÁêÜÂà†Èô§‰ªªÂä°
  const handleDeleteTodo = async (id) => {
    try {
      await deleteTodo(id);
      showMessage('success', '‰ªªÂä°Âà†Èô§ÊàêÂäüÔºÅ');
    } catch (err) {
      showMessage('error', err.message || 'Âà†Èô§‰ªªÂä°Â§±Ë¥•');
    }
  };

  // Â§ÑÁêÜÊ∏ÖÈô§Â∑≤ÂÆåÊàê‰ªªÂä°
  const handleClearCompleted = async () => {
    try {
      const response = await clearCompleted();
      showMessage('success', response.message || 'Â∑≤ÂÆåÊàê‰ªªÂä°Ê∏ÖÈô§ÊàêÂäüÔºÅ');
    } catch (err) {
      showMessage('error', err.message || 'Ê∏ÖÈô§Â∑≤ÂÆåÊàê‰ªªÂä°Â§±Ë¥•');
    }
  };

  // Â§ÑÁêÜÊ∏ÖÁ©∫ÊâÄÊúâ‰ªªÂä°
  const handleClearAll = async () => {
    try {
      const response = await clearAll();
      showMessage('success', response.message || 'ÊâÄÊúâ‰ªªÂä°Ê∏ÖÁ©∫ÊàêÂäüÔºÅ');
    } catch (err) {
      showMessage('error', err.message || 'Ê∏ÖÁ©∫ÊâÄÊúâ‰ªªÂä°Â§±Ë¥•');
    }
  };

  // Â§ÑÁêÜÈîôËØØÈáçËØï
  const handleRetry = () => {
    refreshTodos();
  };

  return (
    <div className="app-container">
      <h1 className="app-title">üìã ÂæÖÂäû‰∫ãÈ°πÁÆ°ÁêÜ</h1>
      
      {/* Ê∂àÊÅØÊèêÁ§∫ */}
      {message.text && (
        <div className={`${message.type}-message`}>
          {message.text}
        </div>
      )}
      
      {/* ÈîôËØØÊèêÁ§∫ */}
      {error && (
        <div className="error-message">
          <strong>ÈîôËØØÔºö</strong>{error}
          <button 
            className="btn btn-outline btn-sm" 
            onClick={handleRetry}
            style={{ marginLeft: '10px' }}
          >
            ÈáçËØï
          </button>
        </div>
      )}
      
      {/* Ê∑ªÂä†‰ªªÂä°Ë°®Âçï */}
      <TodoForm 
        onAddTodo={handleAddTodo}
        loading={loading}
      />
      
      {/* Á≠õÈÄâÊ†è */}
      <FilterBar
        currentFilter={filter}
        onFilterChange={handleFilterChange}
        stats={stats}
      />
      
      {/* ‰ªªÂä°ÂàóË°® */}
      <TodoList
        todos={todos}
        loading={loading}
        onUpdate={handleUpdateTodo}
        onDelete={handleDeleteTodo}
      />
      
      {/* Êìç‰ΩúÊ†è */}
      <ActionBar
        stats={stats}
        onClearCompleted={handleClearCompleted}
        onClearAll={handleClearAll}
        loading={loading}
      />
      
      {/* ÂÖ®Â±ÄÂä†ËΩΩÁä∂ÊÄÅ */}
      {loading && (
        <div style={{ 
          position: 'fixed', 
          top: '50%', 
          left: '50%', 
          transform: 'translate(-50%, -50%)',
          zIndex: 1000,
          background: 'rgba(255, 255, 255, 0.9)',
          padding: '20px',
          borderRadius: '8px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
        }}>
          <LoadingSpinner size="large" text="Â§ÑÁêÜ‰∏≠..." />
        </div>
      )}
    </div>
  );
};

export default TodoApp;
