import React, { memo } from 'react';

const ActionBar = memo(({ 
  stats, 
  onClearCompleted, 
  onClearAll, 
  loading 
}) => {
  const hasCompleted = stats.completed > 0;
  const hasAnyTodos = stats.total > 0;

  const handleClearCompleted = () => {
    if (window.confirm(`Á°ÆÂÆöË¶ÅÂà†Èô§ÊâÄÊúâÂ∑≤ÂÆåÊàêÁöÑ‰ªªÂä°ÂêóÔºü\nËøôÂ∞ÜÂà†Èô§ ${stats.completed} ‰∏™Â∑≤ÂÆåÊàêÁöÑ‰ªªÂä°„ÄÇ`)) {
      onClearCompleted();
    }
  };

  const handleClearAll = () => {
    if (window.confirm(`Á°ÆÂÆöË¶ÅÊ∏ÖÁ©∫ÊâÄÊúâ‰ªªÂä°ÂêóÔºü\nËøôÂ∞ÜÂà†Èô§ ${stats.total} ‰∏™‰ªªÂä°ÔºåÊ≠§Êìç‰Ωú‰∏çÂèØÊí§ÈîÄÔºÅ`)) {
      onClearAll();
    }
  };

  return (
    <div className="action-bar">
      <div className="action-info">
        {hasAnyTodos ? (
          <span>
            ÂÖ± {stats.total} ‰∏™‰ªªÂä°ÔºåÂÖ∂‰∏≠ {stats.completed} ‰∏™Â∑≤ÂÆåÊàêÔºå{stats.pending} ‰∏™ÂæÖÂÆåÊàê
          </span>
        ) : (
          <span>ÊöÇÊó†‰ªªÂä°</span>
        )}
      </div>
      
      <div className="action-buttons">
        {hasCompleted && (
          <button
            className="btn btn-warning"
            onClick={handleClearCompleted}
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="loading-spinner"></span>
                Ê∏ÖÈô§‰∏≠...
              </>
            ) : (
              <>
                <span>üßπ</span>
                Ê∏ÖÈô§Â∑≤ÂÆåÊàê ({stats.completed})
              </>
            )}
          </button>
        )}
        
        {hasAnyTodos && (
          <button
            className="btn btn-danger"
            onClick={handleClearAll}
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="loading-spinner"></span>
                Ê∏ÖÁ©∫‰∏≠...
              </>
            ) : (
              <>
                <span>üóëÔ∏è</span>
                Ê∏ÖÁ©∫ÂÖ®ÈÉ® ({stats.total})
              </>
            )}
          </button>
        )}
      </div>
    </div>
  );
});

ActionBar.displayName = 'ActionBar';

export default ActionBar;
