        <div className="todo-list-header">
          <h2 className="todo-list-title">‰ªªÂä°ÂàóË°®</h2>
          <span className="todo-count">Âä†ËΩΩ‰∏≠...</span>
        </div>
        <div className="loading-container">
          <span className="loading-spinner"></span>
          <span className="loading-text">Ê≠£Âú®Âä†ËΩΩ‰ªªÂä°...</span>
        </div>
      </div>
    );
  }

  if (todos.length === 0) {
    return (
      <div className="todo-list">
        <div className="todo-list-header">
          <h2 className="todo-list-title">‰ªªÂä°ÂàóË°®</h2>
          <span className="todo-count">0 ‰∏™‰ªªÂä°</span>
        </div>
        <div className="empty-state">
          <div className="empty-state-icon">üìù</div>
          <h3 className="empty-state-title">ÊöÇÊó†‰ªªÂä°</h3>
          <p className="empty-state-description">
            ÁÇπÂáª‰∏äÊñπ"Ê∑ªÂä†Êñ∞‰ªªÂä°"ÂºÄÂßãÂàõÂª∫ÊÇ®ÁöÑÁ¨¨‰∏Ä‰∏™‰ªªÂä°ÂêßÔºÅ
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="todo-list">
      <div className="todo-list-header">
        <h2 className="todo-list-title">‰ªªÂä°ÂàóË°®</h2>
        <span className="todo-count">{todos.length} ‰∏™‰ªªÂä°</span>
      </div>
      <div className="todo-list-content">
        {todos.map(todo => (
          <TodoItem
            key={todo.id}
            todo={todo}
            onUpdate={onUpdate}
            onDelete={onDelete}
            loading={loading}
          />
        ))}
      </div>
    </div>
  );
};

export default TodoList;
