import React, { useState, useEffect, useCallback } from 'react';
import TodoForm from './components/TodoForm';
import TodoFilter from './components/TodoFilter';
import TodoList from './components/TodoList';
import TodoActions from './components/TodoActions';
import TodoAPI from './services/api';
import './App.css';

function App() {
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState('all');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // ËÆ°ÁÆóÁªüËÆ°‰ø°ÊÅØ
  const todoStats = {
    total: todos.length,
    active: todos.filter(todo => !todo.completed).length,
    completed: todos.filter(todo => todo.completed).length
  };

  // Âä†ËΩΩÂæÖÂäû‰∫ãÈ°π
  const loadTodos = useCallback(async (filterType = filter) => {
    setLoading(true);
    setError(null);
    
    try {
      const response = await TodoAPI.getTodos(filterType);
      setTodos(response.data || []);
    } catch (err) {
      setError(err.message);
      console.error('Âä†ËΩΩÂæÖÂäû‰∫ãÈ°πÂ§±Ë¥•:', err);
    } finally {
      setLoading(false);
    }
  }, [filter]);

  // ÂàùÂßãÂä†ËΩΩ
  useEffect(() => {
    loadTodos();
  }, [loadTodos]);

  // Ê∑ªÂä†ÂæÖÂäû‰∫ãÈ°π
  const handleAddTodo = async (todoData) => {
    setLoading(true);
    try {
      await TodoAPI.createTodo(todoData);
      await loadTodos(); // ÈáçÊñ∞Âä†ËΩΩÂàóË°®
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // ÂàáÊç¢ÂÆåÊàêÁä∂ÊÄÅ
  const handleToggleTodo = async (id) => {
    setLoading(true);
    try {
      await TodoAPI.toggleTodo(id);
      await loadTodos(); // ÈáçÊñ∞Âä†ËΩΩ‰ª•‰øùÊåÅËøáÊª§Áä∂ÊÄÅ
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Âà†Èô§ÂæÖÂäû‰∫ãÈ°π
  const handleDeleteTodo = async (id) => {
    setLoading(true);
    try {
      await TodoAPI.deleteTodo(id);
      await loadTodos();
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Êõ¥Êñ∞ÂæÖÂäû‰∫ãÈ°π
  const handleUpdateTodo = async (id, updateData) => {
    setLoading(true);
    try {
      await TodoAPI.updateTodo(id, updateData);
      await loadTodos();
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // ËøáÊª§Âô®ÂèòÂåñ
  const handleFilterChange = async (newFilter) => {
    setFilter(newFilter);
    await loadTodos(newFilter);
  };

  // Ê∏ÖÈô§Â∑≤ÂÆåÊàê
  const handleClearCompleted = async () => {
    setLoading(true);
    try {
      await TodoAPI.clearCompleted();
      await loadTodos();
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Ê∏ÖÈô§ÂÖ®ÈÉ®
  const handleClearAll = async () => {
    setLoading(true);
    try {
      await TodoAPI.clearAll();
      await loadTodos();
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Âà∑Êñ∞Êï∞ÊçÆ
  const handleRefresh = () => {
    loadTodos();
  };

  return (
    <div className="app">
      <div className="app__container">
        {/* Â§¥ÈÉ® */}
        <header className="app__header">
          <h1 className="app__title">üìù ÂæÖÂäû‰∫ãÈ°πÁÆ°ÁêÜ</h1>
          <p className="app__subtitle">È´òÊïàÁÆ°ÁêÜÊÇ®ÁöÑÊó•Â∏∏‰ªªÂä°</p>
          <button 
            className="app__refresh"
            onClick={handleRefresh}
            disabled={loading}
            title="Âà∑Êñ∞Êï∞ÊçÆ"
          >
            üîÑ
          </button>
        </header>

        {/* ÈîôËØØÊèêÁ§∫ */}
        {error && (
          <div className="app__error">
            <span className="app__error-icon">‚ö†Ô∏è</span>
            <span className="app__error-message">{error}</span>
            <button 
              className="app__error-close"
              onClick={() => setError(null)}
            >
              ‚úï
            </button>
          </div>
        )}

        {/* Ê∑ªÂä†ÂæÖÂäû‰∫ãÈ°πË°®Âçï */}
        <TodoForm 
          onAddTodo={handleAddTodo}
          loading={loading}
        />

        {/* ËøáÊª§Âô® */}
        <TodoFilter
          currentFilter={filter}
          onFilterChange={handleFilterChange}
          todoStats={todoStats}
        />

        {/* ÂæÖÂäû‰∫ãÈ°πÂàóË°® */}
        <TodoList
          todos={todos}
          loading={loading}
          onToggle={handleToggleTodo}
          onDelete={handleDeleteTodo}
          onUpdate={handleUpdateTodo}
        />

        {/* ÊâπÈáèÊìç‰Ωú */}
        <TodoActions
          onClearCompleted={handleClearCompleted}
          onClearAll={handleClearAll}
          todoStats={todoStats}
          loading={loading}
        />

        {/* È°µËÑö */}
        <footer className="app__footer">
          <p>¬© 2024 ÂæÖÂäû‰∫ãÈ°πÁÆ°ÁêÜÁ≥ªÁªü | ËÆ©ÁîüÊ¥ªÊõ¥ÊúâÂ∫è</p>
        </footer>
      </div>
    </div>
  );
}

export default App;
